<span class="line"><span style="color:#F8F8F2;">        System.out.</span><span style="color:#A6E22E;">println</span><span style="color:#F8F8F2;">(</span><span style="color:#E6DB74;">&quot;QuickSand!&quot;</span><span style="color:#F8F8F2;">);</span></span>
