import{_ as o}from"./gzh.b03adfd2.js";import{_ as a}from"./zsxq.77f6dc86.js";import{_ as n}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as c,c as i,a as e,d as t,b as r,e as p,r as l}from"./app.17f4ab33.js";const _="/assets/b11b3ea50ef11c6531641b862d3b5b69.1dd99f77.jpg",d="/assets/6dac3a9961e69aa86d80de32bdc00987.004b34e0.png",m="/assets/fa0aa02dae219b21de5984371950c3cc.374872be.png",g={},h=p('<p><img src="'+_+'" alt="img" loading="lazy"></p><p>你好，我是悦创。</p><p>今天我来带你进行 KNN 的学习，KNN 的英文叫 K-Nearest Neighbor，应该算是数据挖掘算法中最简单的一种。</p><p>我们先用一个例子体会下。</p><p>假设，我们想对电影的类型进行分类，统计了电影中打斗次数、接吻次数，当然还有其他的指标也可以被统计到，如下表所示。</p><p><img src="'+d+'" alt="img" loading="lazy"></p><p>我们很容易理解《战狼》《红海行动》《碟中谍 6》是动作片，《前任 3》《春娇救志明》《泰坦尼克号》是爱情片，但是有没有一种方法让机器也可以掌握这个分类的规则，当有一部新电影的时候，也可以对它的类型自动分类呢？</p><p>我们可以把打斗次数看成 X 轴，接吻次数看成 Y 轴，然后在二维的坐标轴上，对这几部电影进行标记，如下图所示。</p><p>对于未知的电影 A，坐标为 (x,y)，我们需要看下离电影 A 最近的都有哪些电影，这些电影中的大多数属于哪个分类，那么电影 A 就属于哪个分类。实际操作中，我们还需要确定一个 K 值，也就是我们要观察离电影 A 最近的电影有多少个。</p><p><img src="'+m+'" alt="img" loading="lazy"></p><p>欢迎关注我公众号：AI悦创，有更多更好玩的等你发现！</p><details class="custom-container details"><summary>公众号：AI悦创【二维码】</summary><p><img src="'+o+'" alt="" loading="lazy"></p></details>',12),f={class:"custom-container info"},u=e("p",{class:"custom-container-title"},"AI悦创·编程一对一",-1),b=e("p",null,"AI悦创·推出辅导班啦，包括「Python 语言辅导班、C++ 辅导班、java 辅导班、算法/数据结构辅导班、少儿编程、pygame 游戏开发」，全部都是一对一教学：一对一辅导 + 一对一答疑 + 布置作业 + 项目实践等。当然，还有线下线上摄影课程、Photoshop、Premiere 一对一教学、QQ、微信在线，随时响应！微信：Jiabcdefh",-1),y=e("p",null,"C++ 信息奥赛题解，长期更新！长期招收一对一中小学信息奥赛集训，莆田、厦门地区有机会线下上门，其他地区线上。微信：Jiabcdefh",-1),N={href:"http://wpa.qq.com/msgrd?v=3&uin=1432803776&site=qq&menu=yes",target:"_blank",rel:"noopener noreferrer"},A=e("p",null,"方法二：微信：Jiabcdefh",-1),x=e("p",null,[e("img",{src:a,alt:"",loading:"lazy"})],-1);function v(I,k){const s=l("ExternalLinkIcon");return c(),i("div",null,[h,e("div",f,[u,b,y,e("p",null,[t("方法一："),e("a",N,[t("QQ"),r(s)])]),A]),x])}const V=n(g,[["render",v],["__file","24.html.vue"]]);export{V as default};
