import{_ as l}from"./gzh.1edf1a02.js";import{_ as p}from"./zsxq.e43ad827.js";import{_ as o}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as c,c as i,a as s,d as a,b as n,e,r as d}from"./app.f3ea0e93.js";const u="/assets/image-20220713215158944.fccf37c5.png",g="/assets/HTML概述.90b79d88.png",r="/assets/HTML概述-02-7790609.0c861534.png",k="/assets/image-20220714171356444.a3fd37df.png",m="/assets/1566786065971127.4f3d51d9.png",b="/assets/image-20220716092933554.34dcb521.png",h="/assets/image-20220716093001963.cf7c467a.png",v="/assets/image-20220716094109125.6b5ba8b3.png",_="/assets/image-20220801231401758.8c6c35eb.png",f="/assets/image-20220801231641053.2576adfe.png",y="/assets/image-20220801232129060.6fbfab0d.png",x="/assets/image-20220801232355720.82ddcb8f.png",q="/assets/1565246800318681.380a5fac.png",w={},T=e(`<h2 id="html-是谁-它做什么工作" tabindex="-1"><a class="header-anchor" href="#html-是谁-它做什么工作" aria-hidden="true">#</a> HTML 是谁？它做什么工作？</h2><p>HTML 不是一门编程语言，而是一种用来告知浏览器如何组织网页的<strong>标记语言</strong>。</p><blockquote><p>HTML 称为超文本标记语言，是一种标识性的语言。它包括一系列标签．通过这些标签可以将网络上的文档格式统一，使分散的 Internet 资源连接为一个逻辑整体。HTML 文本是由 HTML 命令组成的描述性文本，HTML 命令可以说明文字，图形、动画、声音、表格、链接等。</p></blockquote><p>HTML <strong>由一系列的元素组成</strong>，这些元素用于组成网页上的模块。如果把网页比喻成房子，网页中使用的元素就像造房子时使用的一块块砖（原材料）。如下面的代码块由 <code>&lt;head&gt;</code> 元素，<code>&lt;title&gt;</code> 元素共同构成了网页的头部模块。</p><div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token doctype"><span class="token punctuation">&lt;!</span><span class="token doctype-tag">DOCTYPE</span> <span class="token name">html</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>html</span> <span class="token attr-name">lang</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>en<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>head</span><span class="token punctuation">&gt;</span></span>
    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>meta</span> <span class="token attr-name">charset</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>UTF-8<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>
    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>title</span><span class="token punctuation">&gt;</span></span>Title<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>title</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>head</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>body</span><span class="token punctuation">&gt;</span></span>

<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>body</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>html</span><span class="token punctuation">&gt;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><strong>注意：</strong> 以上代码块中的 <code>&lt;meta charset =&quot;utf-8&quot;&gt;</code> 并不会出现在网页的头部。它是编码格式，它告诉告诉浏览器用什么方式打开代码。</p><p>头部的效果：</p><p><img src="`+u+'" alt="image-20220713215158944" loading="lazy"></p><h2 id="学习-html-的重点" tabindex="-1"><a class="header-anchor" href="#学习-html-的重点" aria-hidden="true">#</a> 学习 HTML 的重点</h2><p>学习 HTML 把握 2 个重点：<strong>标签和属性。</strong></p><p>HTML 的标签大约几十个。每个标签有几个属性。在初学阶段，你不需要将它全部记下来。你只需记住最常用的十几个即可。</p>',11),L=s("code",null,"w3cschool.cn",-1),M={href:"https://www.w3cschool.cn/htmltags/html-reference.html",target:"_blank",rel:"noopener noreferrer"},H=e('<p>对照列表中的例子用起来就能解决实际的问题了。</p><p>【单选题】以下关于 HTML 描述准确的是？</p><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-0" disabled="disabled"><label class="task-list-item-label" for="task-item-0"> HTML 是超文本标记语言，超文本标记语言是入门级的编程语言。</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-1" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-1"> HTML 是由一系列元素构成的超文本标记语言。</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-2" disabled="disabled"><label class="task-list-item-label" for="task-item-2"> HTML是由一系列元素构成的编程语言。</label></li></ul><h2 id="html-的元素构成及属性" tabindex="-1"><a class="header-anchor" href="#html-的元素构成及属性" aria-hidden="true">#</a> HTML 的元素构成及属性</h2><p>HTML 的元素构成如图：</p><p><img src="'+g+'" alt="HTML概述" loading="lazy"></p><ul><li><p><strong>开始标签：</strong> 开始标签包含元素的名称（本例为 P元素），被一对尖括号“<code>&lt;&gt;</code>”包围。它表示元素从这里开始 ， 本例则表示段落由此开始。</p></li><li><p><strong>结束标签：</strong> 与开始标签相似，结束标签多了符号“<code>/</code>”。它表示元素在这里结束，在本例中表示段落由此结束。</p></li><li><p><strong>内容：</strong> 元素的内容。本例是文本描述：<strong>猎豹其实是一只萌萌哒萨摩耶</strong>。</p></li></ul><p><strong>因此：</strong></p><p><strong>一个完整元素=开始标签+内容+结束标签。</strong></p><p>元素还可以包含属性。<strong>属性就是元素的额外信息</strong>，这些信息不会出现在网页的内容中。</p><p><img src="'+r+'" alt="HTML概述-02" loading="lazy"></p><p>本例中，属性“<code>class</code>”额外说明了 <code>&lt;p&gt;</code> 元素的识别名字，“<code>class</code> ”可以用作识别 <code>&lt;p&gt;</code> 元素的样式以及其它信息。</p><p>元素属性必须包含：</p><ul><li><strong>空格：</strong> 在元素和属性之间必须有一个空格。如果一个元素同时具有多个属性，属性之间也用空格隔开。</li><li><strong>符号“=”：</strong> 属性后面紧跟着一个符号“<code>=</code>”。</li><li><strong>值：</strong> “<code>=</code>”后面必须有一个属性值。它由一对引号<code>&quot;&quot;</code>包围。</li></ul><p>关于属性的值，有三个细节需要注意：</p><ul><li><strong>必须在英文状态下，输入引号。</strong></li><li><strong>可使用双引号也可使用单引号。但是，双引号和单引号不得在一个属性值里混用。</strong></li></ul><p>如：下面的语法是错误的。</p><p><img src="'+k+'" alt="image-20220714171356444" loading="lazy"></p><ul><li><strong>如果在属性值中已使用了双引号，双引号里面的内容还需要引用，则得使用单引号。</strong></li></ul><p><img src="'+m+`" alt="img" loading="lazy"></p><p>【排序】怎么排列才能正确显示 <code>&lt;em&gt;</code> 元素？</p><div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>em</span><span class="token punctuation">&gt;</span></span>我自横刀向天笑，去留肝胆两昆仑。<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>em</span><span class="token punctuation">&gt;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>【单选题】代码：<code>&lt;a href=&quot;https://bornforthis.cn&quot;&gt;随时随地学编程&lt;/a&gt;</code>， 哪部分是属性的值？</p><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-3" disabled="disabled"><label class="task-list-item-label" for="task-item-3"><code>href=&quot;https://www.w3cschool.cn&quot;</code></label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-4" disabled="disabled"><label class="task-list-item-label" for="task-item-4"> 随时随地学编程</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-5" disabled="disabled"><label class="task-list-item-label" for="task-item-5"><code>&lt;a&gt;&lt;/a&gt;</code></label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-6" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-6"><code>&quot;https://bornforthis.cn&quot;</code></label></li></ul><h2 id="元素的嵌套" tabindex="-1"><a class="header-anchor" href="#元素的嵌套" aria-hidden="true">#</a> 元素的嵌套</h2><p><strong>把元素放到其他元素中被替换元素的层叠。</strong></p><p>如：在文本“我叫猎豹。我是一只萨摩耶。”中，提示“萨摩耶”。</p><p>我们可以将 <code>&lt;strong&gt;</code> 元素嵌入在 <code>&lt;p&gt;</code> 元素中。</p>`,28),z={href:"https://bornforthis.cn/web_run/web_column/01.html",target:"_blank",rel:"noopener noreferrer"},A=s("button",{name:"button",style:{color:"black"}},[s("a",{href:"https://bornforthis.cn/web_run/web_column/01.html",target:"_blank"},"尝试一下")],-1),P=e(`<div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>p</span><span class="token punctuation">&gt;</span></span>我叫猎豹。我是一只<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>strong</span><span class="token punctuation">&gt;</span></span>萨摩耶<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>strong</span><span class="token punctuation">&gt;</span></span>。<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>p</span><span class="token punctuation">&gt;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>你发现“<code>萨摩耶</code>”这个词在开头中被<strong>加粗</strong>了，对吗？</p><p>加粗，就是 <code>&lt;strong&gt;</code> 元素发挥的效果。</p><p>从上面的例子可以知道：</p><p><code>&lt;p&gt;标签先开始，&lt;strong&gt;标签再开始。</code></p><p><code>&lt;/strong&gt;</code> 标签<strong>先结束</strong>，<code>&lt;/ p&gt;</code> 标签<strong>再结束</strong>。</p><p>按此顺序，才能制造 <code>&lt;strong&gt;</code> 元素嵌入在 <code>&lt;p&gt;</code> 元素之内的效果。</p><p>如果不按规则编写开始和结束标签，浏览器就会猜测你的代码错误，但它很有可能猜错。</p><p>点击“尝试一下”，看看下面的例子：</p><div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>p</span><span class="token punctuation">&gt;</span></span>我的名字叫猎豹。我是一只<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>strong</span><span class="token punctuation">&gt;</span></span>萨摩耶<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>p</span><span class="token punctuation">&gt;</span></span>。<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>strong</span><span class="token punctuation">&gt;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p><button name="button" style="color:black;"><a href="https://bornforthis.cn/web_runing/web_base/base_03/base_03.html" target="_blank">尝试一下</a></button></p><p>你发现句号被单独列为一行了，对吗？</p><p>这就是层叠顺序错误的结果。</p><p>【排序】将 <code>&lt;em&gt;</code> 元素嵌套在 <code>&lt;p&gt;</code> 元素中强调句子&quot;<code>沐春风,惹红尘一身</code>&quot;中的“<strong>沐春风，</strong>”，正确排序的是？</p><blockquote><p><strong>HTML 着重元素</strong> (<code>&lt;em&gt;</code>) 标记出需要用户着重阅读的内容， <code>&lt;em&gt;</code> 元素是可以嵌套的，嵌套层次越深，则其包含的内容被认定为越需要着重阅读。</p><p><strong>请注意：</strong> 通常地，该元素会被浏览器展示为斜体文本， 但是，它不应该仅仅用于应用斜体样式；为此目的，请使用 CSS 样式。使用 <code>cite</code> 元素标记作品的标题（书籍，戏剧，歌曲等）；它通常也采用斜体样式，但具有不同的含义。使用 <code>&lt;strong&gt;</code> 元素标记比周围文本更重要的文本。</p></blockquote><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-7" disabled="disabled"><label class="task-list-item-label" for="task-item-7"> 惹红尘一身。</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-8" disabled="disabled"><label class="task-list-item-label" for="task-item-8"> 沐春风，</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-9" disabled="disabled"><label class="task-list-item-label" for="task-item-9"><code>&lt;/p&gt;</code></label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-10" disabled="disabled"><label class="task-list-item-label" for="task-item-10"><code>&lt;/em&gt;</code></label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-11" disabled="disabled"><label class="task-list-item-label" for="task-item-11"><code>&lt;em&gt;</code></label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-12" disabled="disabled"><label class="task-list-item-label" for="task-item-12"><code>&lt;p&gt;</code></label></li></ul><div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>p</span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>em</span><span class="token punctuation">&gt;</span></span>沐春风,<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>em</span><span class="token punctuation">&gt;</span></span>惹红尘一身<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>p</span><span class="token punctuation">&gt;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p><button name="button" style="color:black;"><a href="https://bornforthis.cn/web_runing/web_base/base_03/base_03-1.html" target="_blank">尝试一下</a></button></p><h2 id="块级元素和内联元素" tabindex="-1"><a class="header-anchor" href="#块级元素和内联元素" aria-hidden="true">#</a> 块级元素和内联元素</h2><p>在 HTML 中有两种元素类别：块级元素和内联元素。</p><ul><li><strong>块级元素</strong> ：其代码如<strong>块状显示</strong>。块级元素通常用于展示页面上层结构化的内容。如：进行，列表，导航菜单，页脚等。中，但可嵌入在其他块级元素中。</li><li><strong>内联元素：</strong> 它常出现在块级元素中并包裹着一小部分文档内容。内联元素在形式上通常**如行状显示。**它通常出现在一堆文字之间。如，超链接元素 <code>&lt;a&gt;</code> ，专有元素 <code>&lt;em&gt;</code> 元素和 <code>&lt;strong&gt;</code> 元素都是内联元素。</li></ul><p>如下面这段文字介绍：</p><p>第一周杰伦是歌手。第二周杰伦是音乐制作人。第三周杰伦是电影导演。</p><p>点击“<strong>尝试一下</strong>”，查看内联元素 <code>&lt;em&gt;</code> 的效果。</p><div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>em</span><span class="token punctuation">&gt;</span></span>周杰伦是歌手。<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>em</span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>em</span><span class="token punctuation">&gt;</span></span>周杰伦是音乐制作人。<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>em</span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>em</span><span class="token punctuation">&gt;</span></span>周杰伦是电影导演。<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>em</span><span class="token punctuation">&gt;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p><button name="button" style="color:black;"><a href="https://bornforthis.cn/web_runing/web_base/base_03/base_03-2.html" target="_blank">尝试一下</a></button></p><p>内联元素 <code>&lt;em&gt;</code> 使关于周杰伦的介绍如<strong>行状</strong>显示。</p><p>再点击下面的“<strong>尝试一下</strong>”，查看块级元素 <code>&lt;p&gt;</code> 的效果。</p><div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>p</span><span class="token punctuation">&gt;</span></span>周杰伦是歌手。<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>p</span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>p</span><span class="token punctuation">&gt;</span></span>周杰伦是音乐制作人。<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>p</span><span class="token punctuation">&gt;</span></span><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>p</span><span class="token punctuation">&gt;</span></span>周杰伦是电影导演。<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>p</span><span class="token punctuation">&gt;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p><button name="button" style="color:black;"><a href="https://bornforthis.cn/web_runing/web_base/base_03/base_03-3.html" target="_blank">尝试一下</a></button></p><p>相同的内容，块级元素如<strong>块状</strong>显示。</p><h4 id="之前-内联元素运行效果" tabindex="-1"><a class="header-anchor" href="#之前-内联元素运行效果" aria-hidden="true">#</a> 之前：内联元素运行效果</h4><p><img src="`+b+'" alt="image-20220716092933554" loading="lazy"></p><h4 id="之后-块级元素运行效果" tabindex="-1"><a class="header-anchor" href="#之后-块级元素运行效果" aria-hidden="true">#</a> 之后：块级元素运行效果</h4><p><img src="'+h+`" alt="image-20220716093001963" loading="lazy"></p><h2 id="空元素" tabindex="-1"><a class="header-anchor" href="#空元素" aria-hidden="true">#</a> 空元素</h2><p>一个完整元素=开始标签+内容+结束标签</p><p>以上公式并不能适用所有的HTML元素。</p><p>一些元素只有一个标签，元素的标签内附有一些额外信息。这样的元素叫做“<strong>空元素</strong>”。</p><p><code>&lt;img&gt;</code> 就是一个空元素。它通常被插入一张一张指定的图片。</p><p>点击“<strong>尝试一下</strong>”，看看空元素 <code>&lt;img&gt;</code> 的运行效果。</p><div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>img</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>https://bornforthis.cn/gzh.jpg<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p><button name="button" style="color:black;"><a href="https://bornforthis.cn/web_runing/web_base/base_03/base_03-4.html" target="_blank">尝试一下</a></button></p><p>你看到这张图片了吗？</p><p><img src="`+v+`" alt="image-20220716094109125" loading="lazy"></p><p>它是由 <code>&lt;img&gt;</code> 元素中的属性 <code>src =&quot;&quot;</code> 双引号内的地址，向浏览器指定了一张图片。</p><p>空元素 <code>&lt;img&gt;</code>，虽然没有内容和结束标签，但它也依然可以运行出效果。</p><h2 id="html-的文档结构" tabindex="-1"><a class="header-anchor" href="#html-的文档结构" aria-hidden="true">#</a> HTML 的文档结构</h2><p>我们已学习了一些 HTML 元素的基础知识。单独使用这些元素并没有意义。它们需要结合起来，才能形成完整的网页界面。</p><div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token doctype"><span class="token punctuation">&lt;!</span><span class="token doctype-tag">DOCTYPE</span> <span class="token name">html</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>html</span> <span class="token attr-name">lang</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>en<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>head</span><span class="token punctuation">&gt;</span></span>
    <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>title</span><span class="token punctuation">&gt;</span></span>My Puppy<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>title</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>head</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>body</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>p</span><span class="token punctuation">&gt;</span></span>这是我的宠物成长日记页面<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>p</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>body</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>html</span><span class="token punctuation">&gt;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><button name="button" style="color:black;"><a href="https://bornforthis.cn/web_runing/web_base/base_03/base_03-5.html" target="_blank">尝试一下</a></button></p><ul><li><code>&lt;!DOCTYPE html &gt;</code>：<strong>它是文档声明</strong> 。你不需要记忆这行代码，也不需要纠结于它的意义。当它出现时，知道它是一行文档类型的说明即可。</li><li><code>&lt;html&gt;&lt;/html&gt;</code>：<code>&lt;html&gt;</code> 元素是整个完整页面的根元素。</li><li><code>&lt;head&gt;&lt;/head&gt;</code>：<code>&lt;head&gt;</code> 元素是一个容器。它包含了所有 HTML 页面的头部信息。这些信息用户不一定全都看见。这些信息包括搜索关键字，页面描述，CSS 样式等。</li><li><code>&lt;title&gt;&lt;/title&gt;</code>：<code>&lt;title&gt;</code> 元素用来设置页面标题。它出现在浏览器标签上，当你的浏览器标记或收藏页面时，它可用来描述页面。如：<code>bornforthis.cn</code> 的主页面 title</li></ul><p><img src="`+_+'" alt="image-20220801231401758" loading="lazy"></p><ul><li><code>&lt;body&gt;&lt;/body&gt;</code>：<code>&lt;body&gt;</code> 元素包含了访问页面时所有显示在页面上的内容：文本，图片，音频，视频、动画等等。之前的课程，我们介绍过的 <code>&lt;h&gt;</code> 元素、<code>&lt;p&gt;</code> 元素、<code>&lt;img&gt;</code> 元素均可嵌套在 <code>&lt;body&gt;</code> 元素之中形成网页的主体内容。</li></ul><p>综上：<strong>HTML 的文档结构包括网页的 <code>&lt;head&gt;</code> 头部和 <code>&lt;body&gt;</code> 主体两个部分。</strong></p><p>【选词填空】选择合适的元素填写在空格处。</p><p><img src="'+f+`" alt="image-20220801231641053" loading="lazy"></p><div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token doctype"><span class="token punctuation">&lt;!</span><span class="token doctype-tag">DOCTYPE</span> <span class="token name">html</span><span class="token punctuation">&gt;</span></span>

<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>html</span><span class="token punctuation">&gt;</span></span>

<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>head</span><span class="token punctuation">&gt;</span></span> 

My Puppy<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>title</span><span class="token punctuation">&gt;</span></span> 

     

<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>body</span><span class="token punctuation">&gt;</span></span> 

 <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>h1</span><span class="token punctuation">&gt;</span></span>猎豹的成长日记     

<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>p</span><span class="token punctuation">&gt;</span></span>我今天满月啦！主人带我到公园里散步。我好喜欢这里的草地。<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>p</span><span class="token punctuation">&gt;</span></span> 

 <span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>body</span><span class="token punctuation">&gt;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><button name="button" style="color:black;"><a href="https://bornforthis.cn/web_runing/web_base/base_03/base_03-6.html" target="_blank">尝试一下·去补全</a></button></p><details class="custom-container details"><summary>答案</summary><p><img src="`+y+'" alt="image-20220801232129060" loading="lazy"></p></details><h2 id="文档结构中的常见元素" tabindex="-1"><a class="header-anchor" href="#文档结构中的常见元素" aria-hidden="true">#</a> 文档结构中的常见元素</h2><p>为了方便网民阅读网页，网页具有一定的结构，如图：</p><p><img src="'+x+`" alt="image-20220801232355720" loading="lazy"></p><p>以上是一则新闻，它的结构包含标题、图片和段落。</p><p>掌握了 HTML 中的常见元素，你也能把上面的新闻网页效果做出来。步骤如下：</p><ul><li><strong>第1步：使用 <code>&lt;h&gt;</code> 元素</strong> 构成网页标题。标题可分为 6 级：<code>&lt;h1&gt;</code>,<code>&lt;h2&gt;</code>,<code>&lt;h3&gt;</code>,<code>&lt;h4&gt;</code>,<code>&lt;h5&gt;</code>,<code>&lt;h6&gt;</code>。每级标题的字体依次减小。除非是超大型的项目，否则，很少用到 <code>&lt;h4&gt;</code>,<code>&lt;h5&gt;</code>,<code>&lt;h6&gt;</code>。本例仅需 <code>&lt;h1&gt;</code>。</li></ul><p>点击“尝试一下”，查看 <code>&lt;h&gt;</code> 元素运行的效果：</p><div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>h1</span><span class="token punctuation">&gt;</span></span>阿坝首个A级景区完成授牌<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>h1</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>h2</span><span class="token punctuation">&gt;</span></span>阿坝首个A级景区完成授牌<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>h2</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>h3</span><span class="token punctuation">&gt;</span></span>阿坝首个A级景区完成授牌<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>h3</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>h4</span><span class="token punctuation">&gt;</span></span>阿坝首个A级景区完成授牌<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>h4</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>h5</span><span class="token punctuation">&gt;</span></span>阿坝首个A级景区完成授牌<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>h5</span><span class="token punctuation">&gt;</span></span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>h6</span><span class="token punctuation">&gt;</span></span>阿坝首个A级景区完成授牌<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>h6</span><span class="token punctuation">&gt;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><button name="button" style="color:black;"><a href="https://bornforthis.cn/web_runing/web_base/base_03/base_03-7.html" target="_blank">尝试一下</a></button></p><ul><li><strong>第2步：使用 <code>&lt;img&gt;</code> 元素。</strong> 它是空元素，它只有一个标签。它是用来在 <code>&lt;img&gt;</code> 所在的位置插入一张指定的图片。</li></ul><p>下图：</p><p><img src="`+q+`" alt="img" loading="lazy"></p><p>点击“尝试一下”，看看 <code>&lt;img&gt;</code> 元素运行的效果：</p><div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>img</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>https://bornforthis.cn/web_runing/data/base_03/img/base-03-01.png<span class="token punctuation">&quot;</span></span> <span class="token attr-name">alt</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>views<span class="token punctuation">&quot;</span></span><span class="token punctuation">&gt;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p><button name="button" style="color:black;"><a href="https://bornforthis.cn/web_runing/web_base/base_03/base_03-8.html" target="_blank">尝试一下</a></button></p><p>在上述案例中，<code>src=&quot;&quot;</code> 是 <code>&lt;img&gt;</code> 的必要属性。它的值是图片文件的 URL。URL 就是浏览器找到该图的路径。 <code>alt=&quot;&quot;</code> 也是 <code>&lt;img&gt;</code> 的属性。它的值是文本。它规定了当图像无法显示时的替代显示。</p><ul><li><strong>第3步：使用 <code>&lt;p&gt;</code> 元素。</strong> 在前面的知识点中，我们常用它来举例子。它表示段落。它可描述文本。</li></ul><div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>p</span><span class="token punctuation">&gt;</span></span>7月20日上午，在川西高原最美的时节，来自四方的宾朋齐聚在世外桃源—神座村，隆重举行阿坝·神座特种4A级景区授牌仪式。<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;/</span>p</span><span class="token punctuation">&gt;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p><button name="button" style="color:black;"><a href="https://bornforthis.cn/web_runing/web_base/base_03/base_03-9.html" target="_blank">尝试一下</a></button></p><p>通过以上 3 个步骤，你也能制作出简单结构的网页了。</p><p>【单选题】以下关于 src 和 alt 描述准确的是？</p><ul class="task-list-container"><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-13" checked="checked" disabled="disabled"><label class="task-list-item-label" for="task-item-13"> src 是 <code>&lt;img&gt;</code> 元素的必要属性。</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-14" disabled="disabled"><label class="task-list-item-label" for="task-item-14"> src 和 alt 都是 <code>&lt;img&gt;</code> 元素的必要属性。</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-15" disabled="disabled"><label class="task-list-item-label" for="task-item-15"> alt 是属性的值。</label></li><li class="task-list-item"><input type="checkbox" class="task-list-item-checkbox" id="task-item-16" disabled="disabled"><label class="task-list-item-label" for="task-item-16"> src 是图片元素。</label></li></ul><details class="custom-container details"><summary>公众号：AI悦创【二维码】</summary><p><img src="`+l+'" alt="" loading="lazy"></p></details>',83),C={class:"custom-container info"},E=s("p",{class:"custom-container-title"},"AI悦创·编程一对一",-1),I=s("p",null,"AI悦创·推出辅导班啦，包括「Python 语言辅导班、C++ 辅导班、java 辅导班、算法/数据结构辅导班、少儿编程、pygame 游戏开发」，全部都是一对一教学：一对一辅导 + 一对一答疑 + 布置作业 + 项目实践等。当然，还有线下线上摄影课程、Photoshop、Premiere 一对一教学、QQ、微信在线，随时响应！微信：Jiabcdefh",-1),S=s("p",null,"C++ 信息奥赛题解，长期更新！长期招收一对一中小学信息奥赛集训，莆田、厦门地区有机会线下上门，其他地区线上。微信：Jiabcdefh",-1),D={href:"http://wpa.qq.com/msgrd?v=3&uin=1432803776&site=qq&menu=yes",target:"_blank",rel:"noopener noreferrer"},N=s("p",null,"方法二：微信：Jiabcdefh",-1),O=s("p",null,[s("img",{src:p,alt:"",loading:"lazy"})],-1);function Q(V,Y){const t=d("ExternalLinkIcon");return c(),i("div",null,[T,s("p",null,[a("当你在实践中，遇到生疏的标签或属性时，请查阅 "),L,a(" 的手册："),s("a",M,[a("https://www.w3cschool.cn/htmltags/html-reference.html"),n(t)])]),H,s("p",null,[a("点击“"),s("strong",null,[s("a",z,[a("尝试一下"),n(t)])]),a("”，查看效果。："),A]),P,s("div",C,[E,I,S,s("p",null,[a("方法一："),s("a",D,[a("QQ"),n(t)])]),N]),O])}const R=o(w,[["render",Q],["__file","base_03.html.vue"]]);export{R as default};
