import{_ as n}from"./gzh.1edf1a02.js";import{_ as a}from"./zsxq.e43ad827.js";import{_ as r}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as c,c as i,a as t,d as e,b as s,e as l,r as p}from"./app.b13bf937.js";const _="/assets/sep-coding6-feature-640x360_tcm60-107031.8f264f78.jpg",d="/assets/Picture1-1.e47b668a.jpg",h="/assets/image-20221126091728915.f2ae1322.png",m="/assets/image-20221126100921230.0bc267a4.png",g={},u=l('<h2 id="_1-在本地开发-在服务器共享" tabindex="-1"><a class="header-anchor" href="#_1-在本地开发-在服务器共享" aria-hidden="true">#</a> 1. 在本地开发，在服务器共享</h2><p>这是什么意思呢？</p><p>这是说，我们程序员开发网站都是在自己电脑上开发的，我们管这样的环境称为本地。也就是，我们所有的开发操作都是在本地上完成的。</p><p><img src="'+_+'" alt="本地开发的程序员" loading="lazy"></p><p>我们开发的 <code>.html</code> 、<code>.css</code> 、<code>.js</code> 等文件，是需要上传到服务器后，才能被用户看见的。</p><p><img src="'+d+'" alt="img" loading="lazy"></p><p><img src="'+h+'" alt="image-20221126091728915" loading="lazy"></p><p>服务器在上方的右边，这是一个服务器的机群，服务器实际上就是“性能”非常高的——计算机，这些计算机 24h 不断电。因为，一旦断电别的用户就不能看见我们做好的网页了。</p><p><strong>我们的开发人员，就是在本地将 HTML、CSS、Js，把这些开发好，上传这些文件到服务器当中，那服务器中就存储了这些文件。为什么要把文件上传到服务器？——因为，服务器有共享文件的能力。</strong></p><div class="custom-container tip"><p class="custom-container-title">提示</p><p>比如将 <code>bornforthis.html</code> 文件传输到慕课网服务器的 b 文件夹中，此时这个文件就拥有了网址：<code>https://bornforthis.cn/b/bornforthis.html</code> 所有用户都可以访问这个网址，看见我们做的网页啦。</p></div><p>我们要更新或者修改网站，你也只需本地修改再上传覆盖等都可以。</p><h2 id="_2-http-协议" tabindex="-1"><a class="header-anchor" href="#_2-http-协议" aria-hidden="true">#</a> 2. HTTP 协议</h2><p>我们刚刚说，做好的程序或者网页，都会存储到服务器上，那么用户可能会用：笔记本电脑、iPad、手机等来访问我们的网页。</p><p><strong>那么，用户是怎么访问网页的呢？</strong></p><p>相信大家会有类似的经历，其实就是输入网址，也有可能扫描二维码进入网站。——那输入网址就是 <strong>HTTP 请求</strong> 。那什么是 HTTP 请求呢？我们一会给你一个定义。</p><p>服务器会正确识别发来的 HTTP 请求，分析出用户想看的是哪个文件夹中的哪个文件。然后返回给用户所请求的网页：</p><p><img src="'+m+'" alt="image-20221126100921230" loading="lazy"></p><p>这样的我们称为 <strong>HTTP 响应</strong> 。</p>',18),f={href:"https://bornforthis.cn/column/crawler/replenish03.html",target:"_blank",rel:"noopener noreferrer"},b=t("ul",null,[t("li",null,"HTTP 协议（Hypertext Transfer Protocol，超文本传输协议）是互联网数据传输的常见协议。"),t("li")],-1),T=t("details",{class:"custom-container details"},[t("summary",null,"公众号：AI悦创【二维码】"),t("p",null,[t("img",{src:n,alt:"",loading:"lazy"})])],-1),x={class:"custom-container info"},P=t("p",{class:"custom-container-title"},"AI悦创·编程一对一",-1),y=t("p",null,"AI悦创·推出辅导班啦，包括「Python 语言辅导班、C++ 辅导班、java 辅导班、算法/数据结构辅导班、少儿编程、pygame 游戏开发」，全部都是一对一教学：一对一辅导 + 一对一答疑 + 布置作业 + 项目实践等。当然，还有线下线上摄影课程、Photoshop、Premiere 一对一教学、QQ、微信在线，随时响应！微信：Jiabcdefh",-1),k=t("p",null,"C++ 信息奥赛题解，长期更新！长期招收一对一中小学信息奥赛集训，莆田、厦门地区有机会线下上门，其他地区线上。微信：Jiabcdefh",-1),v={href:"http://wpa.qq.com/msgrd?v=3&uin=1432803776&site=qq&menu=yes",target:"_blank",rel:"noopener noreferrer"},H=t("p",null,"方法二：微信：Jiabcdefh",-1),z=t("p",null,[t("img",{src:a,alt:"",loading:"lazy"})],-1);function I(j,q){const o=p("ExternalLinkIcon");return c(),i("div",null,[u,t("p",null,[t("a",f,[e("03-从输入 url 到页面展现发生了什么？"),s(o)])]),b,T,t("div",x,[P,y,k,t("p",null,[e("方法一："),t("a",v,[e("QQ"),s(o)])]),H]),z])}const V=r(g,[["render",I],["__file","txk_14.html.vue"]]);export{V as default};
